import React, {Component} from 'react';
import { parse } from 'querystring';

class App extends Component {
  state = {
    display: 0
  }



  render() {
    let calc = ""
    const submit = (num) => {
      let numConvert = 0;
      calc = calc + num;
      numConvert = parseFloat(calc)
      console.log(numConvert)

    }

    return (
      <div className="App">
        <div className="calc-container">
          <div className="display">
            <p>{this.state.display}</p>
          </div>
          <button className="button text dark-grey clear">C</button>
          <button className="button text dark-grey negative">+/-</button>
          <button onClick={() => {submit("%")}} className="button text dark-grey percent">%</button>
          <button onClick={() => {submit("/")}} className="button text orange divide">/</button>
          <button onClick={() => {submit(7)}} className="button text light-grey seven">7</button>
          <button onClick={() => {submit(8)}} className="button text light-grey eight">8</button>
          <button onClick={() => {submit(9)}} className="button text light-grey nine">9</button>
          <button onClick={() => {submit("*")}} className="button text orange multiply">X</button>
          <button onClick={() => {submit(4)}} className="button text light-grey four">4</button>
          <button onClick={() => {submit(5)}} className="button text light-grey five">5</button>
          <button onClick={() => {submit(6)}} className="button text light-grey six">6</button>
          <button onClick={() => {submit("-")}} className="button text orange subtract">-</button>
          <button onClick={() => {submit(1)}} className="button text light-grey one">1</button>
          <button onClick={() => {submit(2)}} className="button text light-grey two">2</button>
          <button onClick={() => {submit(3)}} className="button text light-grey three">3</button>
          <button onClick={() => {submit("+")}} className="button text orange add">+</button>
          <button onClick={() => {submit(0)}} className="text light-grey zero">0</button>
          <button onClick={() => {submit(".")}} className="button text light-grey decimal">.</button>
          <button onClick={() => {submit("=")}} className="button text orange equals">=</button>
        </div>
      </div>
    );
  }
}

export default App;
